import {Player,Item} from './object.js'
import {map} from './map.js'

function frameUpdate(player,pKeys){
  if(pKeys['w'])player.move(0,-50);
  if(pKeys['s'])player.move(0,50);
  if(pKeys['a'])player.move(-50,0);
  if(pKeys['d'])player.move(50,0);
  console.log(player.pos.x,player.pos.y)

  if(pKeys['.']){
    var p = Player.randomPlayer()
    map.addPlayer(p)
    console.log('added',p.name)
  }
  if(pKeys[',']){
    map.addItem()
    console.log('added 4 items')
  }
  if(pKeys['q']){
    map.deleteAll()
    console.log('deleting')
  }
  player.sw_rot(1*(2*Math.PI/180))
  map.playerUpdate(player)
  map.getItems()
  map.getPlayers()
}
export default frameUpdate
