var ateItemid = null
var killedPlayerid = null
var flag = 0
var delflag = true
function frameUpdate(me,pKeys){
  if(me===null){
    ReactDOM.render(Textbox(), document.getElementById('entername'));
    return
  }
  if(me.dead()){map.kill(me);me=null;console.log('death isn');return}
  else{console.log('notdead',player.hp)}
  if(pKeys['w'])me.addSpeed(0,-1);
  if(pKeys['s'])me.addSpeed(0,1);
  if(pKeys['a'])me.addSpeed(-1,0);
  if(pKeys['d'])me.addSpeed(1,0);
  me.move();
  me.sw_rot(6*(Math.PI/180))
  map.playerUpdate(me)
  map.getItems()
  map.getPlayers()

  for(let i=0;i<map.itemList.length;i++){
    let item = Item.schemaItem(map.itemList[i])
    if(me.trytoeat(item)&&ateItemid != item.id){
      ateItemid = item.id
      item.eaten(me)
      map.ate(item)
    }
  }

  for(let i=0;i<map.playerList.length;i++){
    let player = Player.schemaPlayer(map.playerList[i])
    if(me.trytohit(player)){
      let dmg = me.lv+5
      player.damage(dmg)
      //todo : 이거 옮기기
      if(player.dead() && killedPlayerid != player.id){
        killedPlayerid = player.id
        me.xpup(50)
      }
      //
      else map.damage(player,dmg)
    }
  }

  if(pKeys['.']){
    flag = (flag+1)%50
    console.log(flag)
    if(flag==0){
      var p = new Player('Jane Doe',100,100)
      map.addPlayer(p)
    }
  }
  if(pKeys[',']){
    map.addItem()
    console.log('addItem')
  }
  if(pKeys['q'] && delflag){
    map.deleteAll()
    console.log('deleteall')
