import { Player,Item } from "./object";
import axios from "axios";

const mapConstructor = () => {
    var itemList=[];
    var playerList=[];
    const getItems = () => {
        axios.get(`/api/item`)
        .then(response => {
            this.itemList = response.data;
        })
    }
    const getPlayers = () => {
        axios.get(`/api/player`)
        .then(response => {
            this.playerList = response.data;
        })
    }

    const ate = (item) => {
        axios.delete(`/api/item/${item._id}/ate`)
            .then(() => axios.get('/api/item'))
            .then(response => {
                this.itemList = response.data;
        });
    };

    const kill = (player) => {
        axios.delete(`/api/player/${player._id}/kill`)
            .then(() => axios.get('/api/player'))
            .then(response => {
                this.playerList = response.data;
        });
    };

    const addPlayer = (player) => {
        console(asdf)
        axios.post(`/api/player/${player._id}/add`,{name:player.name})
            .then(() => axios.get('/api/player'))
            .then(response => {
                this.playerList = response.data;
        });
    };

    const damage = (player,dmg) => {
        axios.put(`/api/player/${player._id}/damage`,dmg)
            .then(() => axios.get('/api/player'))
            .then(response => {
                this.playerList = response.data;
        });
    }
};

var map = mapConstructor()

export {map}
