import React, { useRef, useEffect } from 'react'
import draw from './mapdraw.js'

const Canvas = props => {
  let pressedKeys = {};
  function playerMove(){

    if(pressedKeys["KeyW"]){
      props.player.move(0,-10);
      console.log('w pressed')
    }
    if(pressedKeys["KeyA"]){
      props.player.move(-10,0);
      console.log('a pressed')
    }
    if(pressedKeys["KeyS"]){
      props.player.move(0,10);
      console.log('s pressed')
    }
    if(pressedKeys["KeyD"]){
      props.player.move(10,0);
      console.log('d pressed')
    }
  }
  function handleKeyPress(event){
    playerMove();
  } 

  React.useEffect(() => {
    window.addEventListener('keydown', handleKeyPress);

    // cleanup this component
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []);
    const canvasRef = useRef(null)

    useEffect(() => {

    const canvas = canvasRef.current
    const ctx = canvas.getContext('2d')
    let frameCount = 0
    let animationFrameId

    const render = () => {
      frameCount++;
      props.player.sw_angle+=props.player.sw_speed;
      draw(ctx, props.player)
      animationFrameId = window.requestAnimationFrame(render)
    }
    render()

      return () => {
        window.cancelAnimationFrame(animationFrameId)
      }
    }, [draw])

  return <canvas ref={canvasRef} {...props} tabIndex="0" onKeyPress={handleKeyPress}/>
}

export default Canvas
