import axios from "axios";

class mapConstructor{
    constructor(){
        this.itemList=[];
        this.playerList=[];
    }
    getItems(){
        axios.get(`/api/map/item`)
        .then(response => {
            this.itemList = response.data;
        })
    }
    getPlayers(){
        axios.get(`/api/map/player`)
        .then(response => {
            this.playerList = response.data;
        })
    }

    ate(item){
        axios.delete(`/api/map/item/${item._id}/ate`)
            .then(() => axios.get('/api/map/item'))
            .then(response => {
                this.itemList = response.data;
        });
    };

    kill(player){
        axios.delete(`/api/map/player/${player.id}/kill`)
            .then(() => axios.get('/api/map/player'))
            .then(response => {
                this.playerList = response.data;
        });
    };

    addPlayer(player){
        axios.post(`/api/map/player/${player.id}/add`,{name:player.name})
            .then(() => axios.get('/api/map/player'))
            .then(response => {
                this.playerList = response.data;
        });
    };

    damage(player,dmg){
        axios.put(`/api/map/player/${player._id}/damage`,dmg)
            .then(() => axios.get('/api/map/player'))
            .then(response => {
                this.playerList = response.data;
        });
    }
};

var map = new mapConstructor()

export {map}
